pages.styles:
#navbar-ares { background: linear-gradient(90deg, #000000, #2b2b2b); padding: 15px 20px; } 
.navbar-brand { font-size: 1.6rem; font-family: 'Cinzel', serif; letter-spacing: 1px; }
.nav-link { color: #f5d283 !important; font-weight: 500; font-family: 'Cinzel', serif; margin-left: 15px; transition: color 0.3s ease; }
.nav-link:hover { color: #ffffff !important; }
#nav-controls i { color: #f5d283; font-size: 1.2rem; margin-left: 15px; cursor: pointer; }
